mapper.insert:
INSERT INTO board
   SET title = #{title}
     , contents = #{contents}
     , writer = #{writer}

mapper.select:
SELECT board_id AS boardId, title, writer, created_at AS createdAt FROM board
title LIKE '%${search}%' OR writer LIKE '%${search}%' OR contents LIKE '%${search}%'
ORDER BY board_id DESC LIMIT #{startIdx}, #{size}
